<?php/** * Plugin Name: War Manager * Plugin URI: * Description: War Manager * Author: SyNiK4L * Author URI: * Version: 1.0 */function init_manager_admin_page(){    $form_path = path_join(WP_PLUGIN_URL, basename(dirname(__FILE__)) . "/warManagerFunctions.php");    if (!get_option('wmanager_limit'))        $limit = 45;    else        $limit = get_option('wmanager_limit');    ?>    <div style="width: 95%; padding: 10px; margin: 10px;">        <h1>War Manager Settings</h1>        <form action="<?= $form_path ?>" method="post" id="wmanager-settings-form-admin">            <input type='hidden' name='values_changed' value='Y'>            <div id="form_input_fields">                <input type="text" name="wmanager_limit" id="wmanager_limit" value="<?= $limit ?>">            </div>            <span                class="submit-btn">                <?php echo get_submit_button('Save Settings', 'button-primary', 'wmanager_submit', '', ''); ?></span><br><br>            <a class="button button-primary" href="themes.php?page=war-manager" type="button" id="cancel_changes">Cancel                Changes</a>            <?php do_settings_sections('wmanager_setting_options');            settings_fields('wmanager_setting_options'); ?>        </form>        <div id="success_tooltip" style="display:none;">Saved!</div>    </div><?php}//Enqueue Scripts and Stylesfunction init_wmanager_admin_scripts(){    wp_enqueue_script('MainForWManager', path_join(WP_PLUGIN_URL, basename(dirname(__FILE__)) . '/scripts/main.js'), array(), '1.0.0', true);}//Add page to admin cp menufunction init_wmanager_admin_menu(){    add_theme_page('War Manager', 'War Manager', 'edit_theme_options', 'war-manager', 'init_manager_admin_page');}//Create admin panel linkfunction war_action_link($link){    $link[] = '<a href="' . get_admin_url(null, 'themes.php?page=war-manager') . '">Settings</a>';    return $link;}//Initalize Everythingadd_action('admin_menu', 'init_wmanager_admin_menu');add_action('admin_footer', 'init_wmanager_admin_scripts');add_filter('plugin_action_links_' . plugin_basename(__FILE__), 'war_action_link');?>